$fileName = "test_resume.txt"; $userID = "test-user"; $jobTitle = "Software Engineer"; $url = "https://jobspringbackend.azurewebsites.net/process-all/"; $filePath = Join-Path $pwd.Path $fileName; try { $response = Invoke-RestMethod -Uri $url -Method Post -Form @{ file = Get-Item -Path $filePath; user_id = $userID; job_title = $jobTitle } -ContentType "multipart/form-data"; $response | ConvertTo-Json; } catch { $_.Exception.Message; }
